import * as global from './routine_conf';
import {routines} from './routine_loader';

var inter = null;
var secValue = 1000;
var localTime = 0;
var globalTime = 0;
var exercise = true;
var exercisesCompleted = 0;
var exercisesPerGroup = [];
var circuitsCompleted = 0;

var timerCounterSecondsElem = document.querySelector("#timer_counter");
var timerCounterGlobalSecondsElem = document.querySelector("#timer_counter_sec");
var btnStartTimer = document.querySelector("#timer_counter");

btnStartTimer.addEventListener("click", startTimer);

function startTimer() {
    resetTimer();
    timer();
    inter = setInterval(() => {
        timerCounterGlobalSecondsElem.innerHTML = getSecsFormat(++globalTime);;
        timerCounterSecondsElem.innerHTML = getSecsFormat(++localTime);
        if(exercise && localTime >= global.timePerExercise) {
            addTime(exercise);
        } else if(!exercise && localTime >= global.timePerBreak) {
            addTime(exercise);
        }
    }, secValue);
}

function timer() {
    routines.forEach((r, indexr) => {
        exercisesPerGroup.push(r.exercises.length);
    });
}

function addTime(ex) {
    localTime = 0;

    if (exercise) {
        timerCounterSecondsElem.innerHTML = getSecsFormat(localTime);
        document.querySelector(`#exe_routine${circuitsCompleted}_check_${exercisesCompleted}`).classList.add("exercise-completed");
        exercisesCompleted++;
        if (exercisesCompleted >= exercisesPerGroup[circuitsCompleted]) {
            document.querySelector(`#routine_check_${circuitsCompleted}`).classList.add("exercise-completed");
            document.querySelector(`#list_exes${circuitsCompleted}`).style.display = "none";
            let btn_show_hide = document.querySelector(`#btn_show_hide_exes${circuitsCompleted}`);
            btn_show_hide.classList.remove("fa-minus");
            btn_show_hide.classList.add("fa-plus");

            exercisesCompleted = 0;
            circuitsCompleted++;
            if (circuitsCompleted < routines.length) {
                document.querySelector(`#list_exes${circuitsCompleted}`).style.display = "block";
                let btn_show_hide_next = document.querySelector(`#btn_show_hide_exes${circuitsCompleted}`);
                btn_show_hide_next.classList.remove("fa-plus");
                btn_show_hide_next.classList.add("fa-minus");
            }
        }
    }
    exercise = !exercise;
    beep();
    if (circuitsCompleted >= routines.length) {
        timerCounterSecondsElem.innerHTML = "Terminaste!";
        resetTimer();
    }
}

function resetTimer() {
    globalTime = 0;
    exercise = true;
    clearInterval(inter);
}

function getSecsFormat(secs) {
    var sec_num = parseInt(secs);
    var hours   = Math.floor(sec_num / 3600);
    var minutes = Math.floor((sec_num - (hours * 3600)) / 60);
    var seconds = sec_num - (hours * 3600) - (minutes * 60);

    if (hours   < 10) {hours   = "0"+hours;}
    if (minutes < 10) {minutes = "0"+minutes;}
    if (seconds < 10) {seconds = "0"+seconds;}
    return hours+':'+minutes+':'+seconds;
}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    let delay = 400;

    for (let i = 0; i < global.beepsQuantity; i++) {
        if (i === 0) snd.play();
        else {
            setTimeout(() => {
                snd.play(); 
            }, i*delay);
        }
    }
}

